(21, 85, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": [{\r\n    \"@type\": \"Question\",\r\n    \"name\": \"what is HTTP STATUS CODES\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"We Can Say HTTP Response Status Codes Indicate Whether A Specific HTTP Request Has Been Successfully Completed Or Something Went Wrong.\"\r\n    }\r\n  },{\r\n    \"@type\": \"Question\",\r\n    \"name\": \"list of http status codes\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"1xx - Informational\r\n2xx - Success\r\n3xx - Redirection\r\n4xx - Client Error\r\n5xx - Server Error\"\r\n    }\r\n  }]\r\n}\r\n</script>', '2021-08-25 01:20:03'),
(22, 86, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 100 Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"100 Continue Code Says That You Can Continue With Your Current Request To Send Large Body To Server For Example A POST Request.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-06 05:17:42'),
(23, 87, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 101 Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"Http Status Code 1XX Is About Information And Status Of Current Request. This 101 Status Code Is Send By Server When Server Understand And Switching To Newer Version Of Http On Client\'s Request.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-07 04:49:39'),
(24, 88, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 101 Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"This 102 Status Code Processing Shows That Server Is Processing The Client Request But There Is No Result Available At This Moment.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-08 04:07:43'),
(25, 89, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 103 Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"Status Code 103 Is An Informational Response. Used To Return Some Response Headers Before Final HTTP Response 200 Ok From Server. Know more...\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-09 04:49:51'),
(26, 90, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 200 Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 200 (OK) Status Code Indicates That The Request Has Succeeded. A 200 Response Is Cacheable By Default; I.E., Unless Otherwise Indicated By The Method Definition.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-10 04:21:49'),
(27, 91, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What is 201 Status code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 201 (Created) status code indicates that the request has been\r\n    completed and new resources has been created as a result. This  .. .. Know more...\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-11 02:29:42'),
(28, 92, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What is 202 Accepted Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 202 Accepted HyperText Transfer Protocol (HTTP) response status code indicates that the request has been accepted\r\n    for processing, but the processing has not been completed or processing may not have started yet or it might\r\n    be disallowed when processing actually takes place.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-12 02:31:25'),
(29, 93, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": [{\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 203 Non-Authoritative Information ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 203 (Non-Authoritative Information) Status Code Indicates That The Request Was Successful But The Enclosed Packet By Original Server Has Been Modified By Some Proxy.\"\r\n    }\r\n  },{\r\n    \"@type\": \"Question\",\r\n    \"name\": \"Is 203 Response Is Cacheable By Default.\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"yes ,  203 Response Is Cacheable By Default.\"\r\n    }\r\n  }]\r\n}\r\n</script>', '2021-09-13 04:50:05'),
(30, 94, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 204 No Content ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 204 (No Content) Status Code Indicates That The Server Has Successfully Fulfilled The Request And That There Is No Additional Content To Send In The Response Payload(Packet) Body.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-14 08:17:32'),
(31, 95, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What is 205 Reset Content ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 205 (Reset Content) status code indicates that the server has\r\n    fulfilled the request and and server wants to user agent to reset the\r\n    document view.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-21 03:04:15'),
(32, 96, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 3xx Redirect ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 3XX Status Indicates That The Request Is Automatically Redirected To Some Other Uri That Refrenced In Location Header .\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-22 03:08:43'),
(33, 97, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 300 Multiple Choices Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 300 Multiple Choice Status Code Indicated That There More Than One Response Is Available And The User Should Have To Choose Which One To Display.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-23 04:01:19'),
(34, 98, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": [{\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 301 Moved Permanently Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 301 Moved Permanently Status Code Indicates That The Resource Of This Page Is Moved Permanently To New Uri(URL) . From Now All Resource Will Available Through This New One Uri.\"\r\n    }\r\n  },{\r\n    \"@type\": \"Question\",\r\n    \"name\": \"\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"\"\r\n    }\r\n  }]\r\n}\r\n</script>', '2021-09-25 23:47:31'),
(35, 99, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 302 Found Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The HyperText Transfer Protocol (HTTP) 302 Found Redirect Status Response Code Indicates That The Resource Requested Has Been Temporarily Moved To The URI Given By The Location Header.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-27 02:42:35'),
(36, 100, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 303 See Other Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 303 See Other Status Code Indicates That It Is Primarily Used To Allow The Output Of A POST Action To Redirect The User Agent To A Selected Resource that uses get request.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-29 01:16:38'),
(37, 101, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 307 Temporary Redirect Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 307 Temporary Redirect Indicated That The Requested Resource Is Temporarily Available Under Different Url, ANd The User Must Not Change Request Method From Post To Get.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-29 01:58:50'),
(38, 102, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What is 400 Bad Request Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"the 400 Bad request status code indicates that server could not processed the request because something went wrong on client side while sending request.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-29 22:48:32'),
(39, 103, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 401 Unauthorized Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 401 Unauthorized Status Code Indicates That The Server Understood The Request But Refuses To Authorize It Because Of Invalid Authentication Crendentials.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-29 23:16:01'),
(40, 104, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 403 Forbidden Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 403 (Forbidden) Status Code Indicates That The Server Understood The Request But Refuses To Authorize It. Because Of Invalid Authentication Crendentials.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-29 23:25:29'),
(41, 105, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What is 404 Not Found Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 404 (Not Found) status code indicates that the origin server did\r\n    not find a resource for the target resource requested.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-30 23:10:13'),
(42, 106, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What is 405 Method Not Allowed Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The HyperText Transfer Protocol (HTTP) 405 Method Not Allowed response status code indicates that the targeted resource does not support the method you are using.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-30 23:34:15'),
(43, 107, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What is 406 Not Acceptable Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"THe HTTP 406 Not Acceptable client error status code indicates that the server is unable o produce the response that\r\n    is acceptable by user agent.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-09-30 23:54:38'),
(44, 108, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 408 Request Timeout Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 408 (Request Timeout) Status Code Indicates That The Server Did Not Receive A Complete Request Message Within The Maximum Wait Time.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-10-02 00:51:53'),
(45, 109, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 409 Conflict Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 409 Conflict HTTP Status Code Indicates That The Request Could Not Be Completed Due To A Conflict With Resource That Already Available On Server .\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-10-03 00:48:44'),
(46, 110, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 410 Gone Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The 410 status code shows that target\r\n    resource is unavailable at the origin server, and server dont know if it is permanent or temporarily.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-10-04 03:40:00'),
(47, 111, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What is 411 Length Required Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The HTTP 411 Length Required status code indicates that the server\r\n    refuses to accept the request without a defined Content-Length of message body.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-10-05 01:51:38'),
(48, 112, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What is 413 Payload Too Large Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The HTTP 413 Payload too large status code indicates that the request entity is larger than limits defined by server and server continue to close the connection and return a Retry-After header field.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-10-05 02:16:32'),
(49, 113, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 414 URI Too Long Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The HTTP 414 URI Too Long Status Code Indicates That The Server Is Refusing To Service The Request Because The Request-Target Is Longer Than The Server Is Willing To Interpret.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-10-08 00:35:28'),
(50, 114, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 415 Unsupported Media Type Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The HTTP Status Code 415 Unsupported Media Type Indicated That The Origin Server Is Refusing To Complete The Request Because The Payload Is In A Format Not Supported By The Target Resource.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-10-08 22:24:11'),
(51, 115, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What is 426 Upgrade Required Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The HTTP 426 Upgrade Required client error status code indicates that the server not want to complete the request using the current protocol but might be willing to do so after the client upgrades to a different protocol.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-10-08 22:38:36'),
(52, 116, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is Server Error 5xx Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The HTTP 5xx Server Error Class Of Status Code Indicates That The Server Know That There Is An Error And Server Is Incapable To Complete The Request.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-10-10 00:17:37'),
(53, 117, 'faq', '<script type=\"application/ld+json\">\r\n{\r\n  \"@context\": \"https://schema.org\",\r\n  \"@type\": \"FAQPage\",\r\n  \"mainEntity\": {\r\n    \"@type\": \"Question\",\r\n    \"name\": \"What Is 500 Internal Server Error Status Code ?\",\r\n    \"acceptedAnswer\": {\r\n      \"@type\": \"Answer\",\r\n      \"text\": \"The HTTP 500 Internal Server Error Status Code Indicates That Something Unexpected Happened That Preventing The Request To Be Completed.\"\r\n    }\r\n  }\r\n}\r\n</script>', '2021-10-10 22:08:44');

